mapper.select:
SELECT pm.pm_no as pmNo, pm.pm_name as pmName, pm.pm_tel as pmTel, pm.pm_status as pmStatus, pm.pm_region as pmRegion, pm.pm_profile as pmProfile FROM pickupman pm
where pm.pm_region = #{pmRegion}
order by pm_no desc

select count(*) 
from pickupman

	where pm_region = #{pmRegion}

mapper.update:
UPDATE pickupman
SET pm_status = #{pmStatus}
WHERE pm_no = #{pmNo}
